mapper.insert:

insert into notice_phone_white_list (phone, send_limit, app_code,
  app_name, create_time, start_time,
  update_time, end_time, is_enabled
  )
values (#{phone,jdbcType=VARCHAR}, #{sendLimit,jdbcType=INTEGER}, #{appCode,jdbcType=VARCHAR},
  #{appName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{startTime,jdbcType=TIMESTAMP},
  #{updateTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{isEnabled,jdbcType=INTEGER}
  )

mapper.select:
select

from notice_phone_white_list


    and phone = #{phone,jdbcType=VARCHAR}



    and app_code = #{appCode,jdbcType=VARCHAR}



    and is_enabled = #{isEnabled,jdbcType=INTEGER}

  ${limitClause}

select count(1)
from notice_phone_white_list


    and phone = #{phone,jdbcType=VARCHAR}



    and app_code = #{appCode,jdbcType=VARCHAR}



    and is_enabled = #{isEnabled,jdbcType=INTEGER}

  ${limitClause}
