mapper.select:
select
id, code, name, leader,team_members, remark, enabled, create_time
from mnmp_notify.notice_app


        and id = #{id}


        and code = #{code}


        and name = #{name}


        and leader = #{leader}


        and remark = #{remark}


        and enabled = #{enabled}


        and create_time = #{createTime}


        and (
        FIND_IN_SET(#{userName},leader)
        or
        FIND_IN_SET(#{userName},team_members)
        )
